mapper.update:
	UPDATE event SET name=#{name}, content=#{content}, imageFilename=#{imageFilename},
 		sDate=#{sdate}, eDate=#{edate}
	WHERE eventNum = #{eventNum}

mapper.select:
	SELECT eventNum, name, content, sDate, eDate, imageFilename, hitCount 
 	from event 
 	WHERE eventNum = #{eventNum}
